mapper.insert:
insert into user(
	password, name, nickname, email, eatlike1, eatlike2, eatlike3, playlike1, playlike2, playlike3
) values (
	#{password}, #{name}, #{nickname}, #{email}, #{eatlike1}, #{eatlike2}, #{eatlike3}, #{playlike1},
	#{playlike2},#{playlike3}
)

mapper.select:
select id
from user
where nickname = #{nickname}
